import React,{ useEffect,useState }  from "react";
import "./Home.css";
import Header from "../../Components/Header/Header";
import Footer from "../../Components/Footer/Footer";
import SpinnerLoader from "../../Components/SpinnerLoader/SpinnerLoader";
import {
  HTTP_SERVICE_CALL,
} from "../../Components/ApiProvider/ApiProvider";

function Home() {
  const [products,setProducts] = useState([]);
  const [loading,setLoading] = useState(false);

  useEffect(()=>{
    getProducts();
  },[])


  const getProducts = async () => {
    setLoading(true);
    try {
      let response = await HTTP_SERVICE_CALL(
        "https://fakestoreapi.com/products",
        "GET",
        {},
        ""
      );
      if (response && (response.status === 200)) {
        setProducts(response.data);
        setLoading(false);
      }
    } catch (error) {
      console.log(error);
      setLoading(false);
    }
  }

  return (
    <>
      <Header/>
      <main class="container-fluid">
      <div class="text-center">
      <div class="row">
      <div class="col-sm-8">col-sm-8</div>
      <div class="col-sm-4">col-sm-4</div>
    </div>
      <div class="row search_box">
        <div class="col-1 col-sm-2">Search</div>
        <div class="col-4 col-sm-10">
        <input type="text" class="form-control form-control-color" id="exampleColorInput" value="" title="Choose your color"/>
        </div>
        <div class="col-7 col-sm-0"></div>
      </div>
       </div> 
      
      {loading && <SpinnerLoader/>}   
      <div class="row row-cols-1 row-cols-md-4 g-4">
       {products.map((element,key)=>(
              <div class="col products">
                <div class="card">
                  <img src={element.image} class="card-img-top" alt="..."/>
                  <div class="card-body">
                    <h5 class="card-title">{element.title}</h5>
                    <p class="card-text">{element.description}</p>
                  </div>
                </div>
              </div>
       ))}
      </div>
      </main>
     <Footer/>
     </>
  );
}
export default Home;
